section.para:
ComesAfterInspectionResultProcessor sorts inspection results according
to the comes-after attribute. This attribute can be created using the
@UiComesAfter annotation (among other ways). For example, the following inspection result...

section.programlisting:
import org.metawidget.inspector.annotation.*;

public class ContactBean {

	@UiAction
	@UiLabel( "#{contact.readOnly ? 'Back' : null}" )
	public void cancel() { ... }
}

import org.metawidget.inspector.annotation.*;

public class ContactDialog {

	@UiAction
	@UiLabel( "${if ( this.readOnly ) 'Back'}" )
	public void cancel() { ... }
}

import org.metawidget.inspector.annotation.*;

public class ContactController {

	@UiAction
	@UiLabel( "${contactController.readOnly ? 'Back' : null}" )
	public String cancel() { ... }
}

var mw = new metawidget.Metawidget( document.getElementById( 'metawidget' ), {
	
	inspectionResultProcessors: [ function( inspectionResult, mw, toInspect, type, names ) {

		var xhr = new XMLHttpRequest();
		xhr.open( "GET", "rest/metadata/get", true );
		
		xhr.onreadystatechange = function () {
		    if ( xhr.readyState == 4 && xhr.status == 200 ) {
		    	
		    	inspectionResult = metawidget.util.combineInspectionResults( inspectionResult,
		    		JSON.parse( xhr.responseText ));
		    	
		    	// Resume Metawidget operation
		    	
		    	mw.buildWidgets( inspectionResult );
		    }
		}
		
		xhr.send();
		
		// Return nothing to suspend Metawidget operation
	} ]
} );

$( "#metawidget" ).metawidget( {

	inspectionResultProcessors: [ function( inspectionResult, mw, toInspect, type, names ) {

		$.ajax( 'rest/metadata/get' ).done( function ( data ) {

			inspectionResult = metawidget.util.combineInspectionResults( inspectionResult, JSON.parse( data ));

			// Resume Metawidget operation
			
	    	mw._refresh( inspectionResult );
		} );

		// Return nothing to suspend Metawidget operation
	} ]
} );

$scope.metawidgetConfig = {

	inspectionResultProcessors: [ function( inspectionResult, mw, toInspect, type, names ) {

		$http.get( 'rest/metadata/get' ).then( function( result ) {

			inspectionResult = metawidget.util.combineInspectionResults( inspectionResult, result.data );

			// Resume Metawidget operation
			
			mw.buildWidgets( inspectionResult );
		} );
		
		// Return nothing to suspend Metawidget operation
	} ]
};
